/**
 * @brief map_init 初始化地图
 * @param data_pointer 如果使用默认地图则传入NULL
 */
void map_init(void *data_pointer)
{
    if(data_pointer)
    {
        memcpy(g_map_scene, data_pointer, MAP_HEIGHT*MAP_WIDTH) ;
    }
}


void map_darw()
{
    for(short row =0; row < MAP_HEIGHT; row ++)
    {
        for(short col =0; col  < MAP_WIDTH; col++)
        {
            write_char(col, row, wall_pic[g_map_scene[row][col]], F_H_WHITE) ;
        }
    }
}
